DROP TABLE IF EXISTS `fas_fund_day_top`;
CREATE TABLE `fas_fund_day_top` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `created_at` datetime DEFAULT NULL COMMENT '创建时间',
