  `week_change` decimal(10,4) DEFAULT NULL COMMENT '近一周(百分比)',
  `mouth_change` decimal(10,4) DEFAULT NULL COMMENT '近一月(百分比)',
  `three_mouth_change` decimal(10,4) DEFAULT NULL COMMENT '近3个月(百分比)',
  `six_mouth_change` decimal(10,4) DEFAULT NULL COMMENT '近6个月(百分比)',
  `year_change` decimal(10,4) DEFAULT NULL COMMENT '近1年(百分比)',
  `two_year_change` decimal(10,4) DEFAULT NULL COMMENT '近2年(百分比)',
  `three_year_change` decimal(10,4) DEFAULT NULL COMMENT '近2年(百分比)',
  `current_change` decimal(10,4) DEFAULT NULL COMMENT '今年来(百分比)',
