  KEY `idx_fas_fund_day_top_top_date` (`top_date`),
  KEY `idx_fas_fund_day_top_deleted_at` (`deleted_at`),
  KEY `idx_fas_fund_day_top_fund_code` (`fund_code`),
  KEY `idx_fas_fund_day_top_fund_name` (`fund_name`)
